import React, { useState } from 'react';
import axios from 'axios';
import CarForm from './components/Carro/Carro';
import CarInfo from './components/Resultado/Resultado';
import './App.css'

const App = () => {
  const [carInfo, setCarInfo] = useState(null);
  const [error, setError] = useState(null);


  const searchCar = ({ ano, marca, modelo }) => {
       axios.get(`http://veiculos.fipe.org.br/api/veiculos/ConsultarValorComTodosParametros?codigoMarca=${marca}&codigoModelo=${modelo}&anoModelo=${ano}`)
      .then(response => {
        setCarInfo(response.data);
      })
      .catch(error => {
        console.error('Erro ao buscar informações do carro:', error);
        setCarInfo(null);
        setError('Erro ao buscar informações do carro. Por favor, verifique os dados e tente novamente.');
      });
  };

  return (
    <div>
      <h1>Busca na Tabela FIPE</h1>
      <CarForm onSearch={searchCar} />
      {error && <p style={{ color: 'red'}}>{error}</p>}
      {carInfo && <CarInfo carInfo={carInfo} />}
    </div>
  );
};

export default App;
