// src/components/Carro/Carro.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CarForm = ({ onSearch }) => {
  const [anos, setAnos] = useState([]);
  const [marcas, setMarcas] = useState([]);
  const [modelos, setModelos] = useState([]);

  const [anoSelecionado, setAnoSelecionado] = useState('');
  const [marcaSelecionada, setMarcaSelecionada] = useState('');
  const [modeloSelecionado, setModeloSelecionado] = useState('');

  useEffect(() => {
    // Carrega as opções de anos, marcas e modelos ao montar o componente
    loadAnos();
    loadMarcas();
    // Pode carregar as marcas primeiro e depois carregar os modelos com base na marca selecionada
  }, []);

  const loadAnos = async () => {
    try {
      const response = await axios.get('http://veiculos.fipe.org.br/api/veiculos/ConsultarTabelaDeReferencia');
      setAnos(response.data);
    } catch (error) {
      console.error('Erro ao carregar anos:', error);
    }
  };

  const loadMarcas = async () => {
    try {
      const response = await axios.get('https://parallelum.com.br/fipe/api/v1/carros/marcas');
      setMarcas(response.data);
    } catch (error) {
      console.error('Erro ao carregar marcas:', error);
    }
  };

  const loadModelos = async () => {
    try {
      const response = await axios.get(`https://parallelum.com.br/fipe/api/v1/carros/marcas/59/modelos${marcaSelecionada}`);
      setModelos(response.data.modelos);
    } catch (error) {
      console.error('Erro ao carregar modelos:', error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch({ ano: anoSelecionado, marca: marcaSelecionada, modelo: modeloSelecionado });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Ano:
        <select value={anoSelecionado} onChange={(e) => setAnoSelecionado(e.target.value)}>
          <option value="">Selecione o ano</option>
          {anos.map(ano => (
            <option key={ano.Codigo} value={ano.Codigo}>{ano.Mes}</option>
          ))}
        </select>
      </label>

      <label>
        Marca:
        <select value={marcaSelecionada} onChange={(e) => {
          setMarcaSelecionada(e.target.value);
          setModeloSelecionado(''); // Limpa o modelo ao alterar a marca
          loadModelos();
        }}>
          <option value="">Selecione a marca</option>
          {marcas.map(marca => (
            <option key={marca.Codigo} value={marca.Codigo}>{marca.Nome}</option>
          ))}
        </select>
      </label>

      <label>
        Modelo:
        <select value={modeloSelecionado} onChange={(e) => setModeloSelecionado(e.target.value)}>
          <option value="">Selecione o modelo</option>
          {modelos.map(modelo => (
            <option key={modelo.Codigo} value={modelo.Codigo}>{modelo.Nome}</option>
          ))}
        </select>
      </label>

      <button type="submit">Buscar</button>
    </form>
  );
};

export default CarForm;
