import "./App.css";
import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const url = "https://parallelum.com.br/fipe/api/v1/carros/marcas/59/modelos";
  const [data, setData] = useState([]);

  const fetchInfo = () => {
    return axios.get(url)
      .then((res) => setData(prevData => [...prevData, ...res.data]))
      .catch((error) => {
        console.error("Erro ao buscar dados:", error);
      });
  };

  useEffect(() => {
    fetchInfo();
  }, []);

  return (
    <div className="App">
      <h1 style={{ color: "green" }}>Using Axios Library to Fetch Data</h1>
      <center>
        {data.length > 0 ? (
          data.map((dataObj, index) => (
            <div
              key={index}
              style={{
                width: "15em",
                backgroundColor: "#CD8FFD",
                padding: 2,
                borderRadius: 10,
                marginBlock: 10,
              }}
            >
              <p style={{ fontSize: 20, color: 'black' }}>{dataObj.name}</p>
            </div>
          ))
        ) : (
          <p>Loading...</p>
        )}
      </center>
    </div>
  );
}

export default App;
