import React, { useState, useEffect } from 'react';

function App() {
  const [carBrands, setCarBrands] = useState([]);
  const [carModels, setCarModels] = useState([]);
  const [carYears, setCarYears] = useState([]);
  const [carValue, setCarValue] = useState('');
  const [selectedBrand, setSelectedBrand] = useState('');
  const [selectedModel, setSelectedModel] = useState('');
  const [selectedYear, setSelectedYear] = useState('');

  useEffect(() => {
    // Fetch car brands from the API
    axios.get('https://parallelum.com.br/fipe/api/v1/carros/marcas')
      .then(response => setCarBrands(response.data))
      .catch(error => console.error('Error fetching car brands:', error));
  }, []);

  useEffect(() => {
    // Fetch car models when the selected brand changes
    if (selectedBrand) {
      axios.get(`https://parallelum.com.br/fipe/api/v1/carros/marcas/${selectedBrand}/modelos`)
        .then(response => setCarModels(response.data.modelos))
        .catch(error => console.error('Error fetching car models:', error));
    }
  }, [selectedBrand]);

  useEffect(() => {
    // Fetch car years when the selected model changes
    if (selectedBrand && selectedModel) {
      axios.get(`https://parallelum.com.br/fipe/api/v1/carros/marcas/${selectedBrand}/modelos/${selectedModel}/anos`)
        .then(response => setCarYears(response.data))
        .catch(error => console.error('Error fetching car years:', error));
    }
  }, [selectedBrand, selectedModel]);

  useEffect(() => {
    // Fetch car value when the selected year changes
    if (selectedBrand && selectedModel && selectedYear) {
      axios.get(`https://parallelum.com.br/fipe/api/v1/carros/marcas/${selectedBrand}/modelos/${selectedModel}/anos/${selectedYear}`)
        .then(response => setCarValue(response.data.Valor))
        .catch(error => console.error('Error fetching car value:', error));
    }
  }, [selectedBrand, selectedModel, selectedYear]);

  const handleBrandChange = (event) => {
    const newSelectedBrand = event.target.value;
    setSelectedBrand(newSelectedBrand);
    // Reset selected model, year, and value when the brand changes
    setSelectedModel('');
    setSelectedYear('');
    setCarValue('');
  };

  const handleModelChange = (event) => {
    const newSelectedModel = event.target.value;
    setSelectedModel(newSelectedModel);
    // Reset selected year and value when the model changes
    setSelectedYear('');
    setCarValue('');
  };

  const handleYearChange = (event) => {
    const newSelectedYear = event.target.value;
    setSelectedYear(newSelectedYear);
    // Reset selected value when the year changes
    setCarValue('');
  };

  return (
    <div className="App">
      <h1>Car Brands, Models, Years, and Value App</h1>

      <label>Select a Car Brand:</label>
      <select value={selectedBrand} onChange={handleBrandChange}>
        <option value="">Select a brand</option>
        {carBrands.map(brand => (
          <option key={brand.codigo} value={brand.codigo}>
            {brand.nome}
          </option>
        ))}
      </select>

      {selectedBrand && (
        <div>
          <label>Select a Car Model:</label>
          <select value={selectedModel} onChange={handleModelChange}>
            <option value="">Select a model</option>
            {carModels.map(model => (
              <option key={model.codigo} value={model.codigo}>
                {model.nome}
              </option>
            ))}
          </select>

          {selectedModel && (
            <div>
              <label>Select a Car Year:</label>
              <select value={selectedYear} onChange={handleYearChange}>
                <option value="">Select a year</option>
                {carYears.map(year => (
                  <option key={year.codigo} value={year.codigo}>
                    {year.nome}
                  </option>
                ))}
              </select>

              {selectedYear && (
                <div>
                  <h2>Selected Details:</h2>
                  <p>Brand Code: {selectedBrand}</p>
                  <p>Brand Name: {carBrands.find(brand => brand.codigo === selectedBrand)?.nome}</p>
                  <p>Model Code: {selectedModel}</p>
                  <p>Model Name: {carModels.find(model => model.codigo === selectedModel)?.nome}</p>
                  <p>Year Code: {selectedYear}</p>
                  <p>Year: {carYears.find(year => year.codigo === selectedYear)?.nome}</p>
                  <p>Value: {carValue}</p>
                </div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
