  import { useState } from "react";

  const fetchData = async () => {
  const marcasResponse =  fetch('https://api.fipe.ws/v1/carros/marcas');
  const marcas =  marcasResponse.json();
  const modelosResponse =  fetch('https://api.fipe.ws/v1/carros/marcas/1/modelos');
  const modelos =  modelosResponse.json();
  const anosResponse =  fetch('https://api.fipe.ws/v1/carros/marcas/1/modelos/1/anos');
  const anos =  anosResponse.json();
  return { marcas, modelos, anos };
};

const App = () => {
  const [marca, setMarca] = useState('');
  const [modelo, setModelo] = useState('');
  const [ano, setAno] = useState('');
  const [data, setData] = useState(null);
  const { marcas, modelos, anos } = fetchData();

  const handleSubmit = async () => {
    const data = await fetchData(marca, modelo, ano);
    setData(data);
  };

  return (
    <div>
      <select value={marca} onChange={e => setMarca(e.target.value)}>
        {marcas.map(marca => (
          <option key={marca.codigo} value={marca.codigo}>{marca.nome}</option>
        ))}
      </select>
      <select value={modelo} onChange={e => setModelo(e.target.value)}>
        {modelos.map(modelo => (
          <option key={modelo.codigo} value={modelo.codigo}>{modelo.nome}</option>
        ))}
      </select>
      <select value={ano} onChange={e => setAno(e.target.value)}>
        {anos.map(ano => (
          <option key={ano.codigo} value={ano.codigo}>{ano.nome}</option>
        ))}
      </select>
      <button onClick={handleSubmit}>Buscar</button>
      {data && (
        <pre>{JSON.stringify(data, null, 2)}</pre>
      )}
    </div>
  );
};

export default App;
