import { useState, useEffect } from "react";

const BuscaAPI = async () => {
  const marcasResponse = await fetch('https://parallelum.com.br/fipe/api/v1/carros/marcas');
  const marcas = await marcasResponse.json();
  const modelosResponse = await fetch('https://parallelum.com.br/fipe/api/v1/carros/marcas/59/modelos');
  const modelos = await modelosResponse.json();
  const anosResponse = await fetch('https://parallelum.com.br/fipe/api/v1/carros/marcas/59/modelos/5940/anos');
  const anos = await anosResponse.json();
  return { marcas, modelos, anos };
};

const App = () => {
  const [marca, setMarca] = useState('');
  const [modelo, setModelo] = useState('');
  const [ano, setAno] = useState('');
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [marcas, setMarcas] = useState([]);  // Adicione estas linhas
  const [modelos, setModelos] = useState([]); // Adicione estas linhas
  const [anos, setAnos] = useState([]);       // Adicione estas linhas

  useEffect(() => {
    const fetchData = async () => {
      try {
        const apiData = await BuscaAPI();
        setMarcas(apiData.marcas);   // Atualize estas linhas
        setModelos(apiData.modelos); // Atualize estas linhas
        setAnos(apiData.anos);       // Atualize estas linhas
        setData(apiData);
      } catch (error) {
        console.error("Erro ao buscar dados da API:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // ...

  return (
    <div>
      <section>
        <select value={marca} onChange={(e) => handleInputChange(e, setMarca)}>
          {marcas && marcas.map(marca => (
            <option key={marca.codigo} value={marca.codigo}>{marca.nome}</option>
          ))}
        </select>
        <select value={modelo} onChange={(e) => handleInputChange(e, setModelo)}>
          {modelos && modelos.map(modelo => (
            <option key={modelo.codigo} value={modelo.codigo}>{modelo.nome}</option>
          ))}
        </select>
        <select value={ano} onChange={(e) => handleInputChange(e, setAno)}>
          {anos && anos.map(ano => (
            <option key={ano.codigo} value={ano.codigo}>{ano.nome}</option>
          ))}
        </select>
        <button onClick={handleSubmit} disabled={loading}>
          {loading ? 'Carregando...' : 'Buscar'}
        </button>
        {data && (
          <pre>{JSON.stringify(data, null, 2)}</pre>
        )}
      </section>
    </div>
  );
};

export default App;
